import firebase from 'firebase'
import 'firebase/firestore'

// firebase init goes here
const config = {
    apiKey: "AIzaSyAWr372l0gzRQ_PH6e7GHUpnv5Z6c8bCHg",
    authDomain: "about-me-582c5.firebaseapp.com",
    databaseURL: "https://about-me-582c5.firebaseio.com",
    projectId: "about-me-582c5",
    storageBucket: "about-me-582c5.appspot.com",
    messagingSenderId: "920884952929",
}
firebase.initializeApp(config)

// firebase utils
const db = firebase.firestore()
const auth = firebase.auth()
const currentUser = auth.currentUser


// firebase collections
const questionsCollection = db.collection('questions')
const quizesCollection = db.collection('quizes')
const usersCollection = db.collection('users')


export {
    db,
    auth,
    currentUser,
    questionsCollection,
    quizesCollection,
    usersCollection,
}


// WEBPACK FOOTER //
// ./src/firebaseConfig.js
